<?php
 namespace UCSS; class Speed { public static function init() { Speed\CSS::init(); add_action( 'init',function() { ob_start(array(__CLASS__, 'process_output')); }); } public static function process_output($output) { $output = Speed\CSS::rewrite_css($output); $output = self::rewrite_html($output); return $output; } public static function rewrite_html($html) { if (!strstr($html, '<html')) { return $html; } return $html; } } 